import { useState, useRef, useEffect } from 'react'
import './slider.css'
import './thumb.css'

function MusicCard({ percentage = 0, onChange }) {
  const [position, setPosition] = useState(0)
  const [marginLeft, setMarginLeft] = useState(0)
  const [progressBarWidth, setProgressBarWidth] = useState(0)

  const rangeRef = useRef()
  const thumbRef = useRef()

  useEffect(() => {
    const rangeWidth = rangeRef.current.getBoundingClientRect().width
    const thumbWidth = thumbRef.current.getBoundingClientRect().width
    const centerThumb = (thumbWidth / 100) * percentage * -1
    const centerProgressBar =
      thumbWidth + (rangeWidth / 100) * percentage - (thumbWidth / 100) * percentage
    setPosition(percentage)
    setMarginLeft(centerThumb)
    setProgressBarWidth(centerProgressBar)
    console.log(rangeWidth,"  ",thumbWidth,"  ",centerThumb);

  }, [percentage])


    return(
<div class="main">
    <p id="logo"><i class="fa fa-music"></i>Music</p> 
    <div class="show_song_no">
        <p id="present">1</p>
        <p>/</p>
        <p id="total">5</p>
    </div>
    <div class="left"> <img id="track_image"/>
        <div class="volume">
            <p id="volume_show">90</p> <i class="fa fa-volume-up" aria-hidden="true" onclick="mute_sound()" id="volume_icon"></i> <input type="range" min="0" max="100" value="90" onchange="volume_change()" id="volume"/>
        </div>
    </div>
    <div class="right">
        <div class="song_detail">
            <p id="title">title.mp3</p>
            <p id="artist">Artist name</p>
        </div>
        <div class="middle"> <button onclick="previous_song()" id="pre"><i class="fa fa-step-backward" aria-hidden="true"></i></button> <button onclick="justplay()" id="play"><i class="fa fa-play" aria-hidden="true"></i></button> <button onclick="next_song()" id="next"><i class="fa fa-step-forward" aria-hidden="true"></i></button> </div>
        <div className='slider-container'>
      <div
        className='progress-bar-cover'
        style={{
          width: `${progressBarWidth}px`
        }}
      ></div>
      <div
        className='thumb'
        ref={thumbRef}
        style={{
          left: `${position}%`,
          marginLeft: `${marginLeft}px`
        }}
      ></div>
      <input
        type='range'
        value={position}
        ref={rangeRef}
        step='0.01'
        className='range'
        onChange={onChange}
      />
    </div>
    </div>
</div>
    )
}
export default MusicCard;
