import { useState, useRef } from 'react'
//import song from './Suncrown - Legend of the Forgotten Centuries.mp3'
import Slider from './Slider'
import './combine/music'
import './MusicCard.css'
import ControlPanel from './ControlPanel'
function MusicCard() {
    const [percentage, setPercentage] = useState(0)
    const [isPlaying, setIsPlaying] = useState(false)
    const [duration, setDuration] = useState(0)
    const [currentTime, setCurrentTime] = useState(0)
    const [src1,setSrc] = useState('')
    const [musicNum,setNum] =useState(1)
    const audioRef = useRef()
    const ChangeSrc = ()=>{
      let m=musicNum;
      setNum(m+1);
      let sr="music"+m+".mp3";
      setSrc(sr);
    }
    const onChange = (e) => {
      const audio = audioRef.current
      audio.currentTime = (audio.duration / 100) * e.target.value
      setPercentage(e.target.value)
    }
  
    const play = () => {
      const audio = audioRef.current
      audio.volume = 0.1
  
      if (!isPlaying) {
        setIsPlaying(true)
        audio.play()
      }
  
      if (isPlaying) {
        setIsPlaying(false)
        audio.pause()
      }
    }
  
    const getCurrDuration = (e) => {
      const percent = ((e.currentTarget.currentTime / e.currentTarget.duration) * 100).toFixed(2)
       
  
      setPercentage(+percent)
      //console.log(+percent);
      setCurrentTime(e.currentTarget.currentTime.toFixed(2))
    }
  
    function secondsToHms(seconds) {
        if (!seconds) return '00m 00s'
    
        let duration = seconds
        let hours = duration / 3600
        duration = duration % 3600
    
        let min = parseInt(duration / 60)
        duration = duration % 60
    
        let sec = parseInt(duration)
    
        if (sec < 10) {
          sec = `0${sec}`
        }
        if (min < 10) {
          min = `0${min}`
        }
    
        if (parseInt(hours, 10) > 0) {
          return `${parseInt(hours, 10)}h ${min}m ${sec}s`
        } else if (min == 0) {
          return `00m ${sec}s`
        } else {
          return `${min}m ${sec}s`
        }
      }
    return(<>
    
    <div class="container">
	<div class="row">
		<div class="col-sm-12 col-lg-6">
			<div class="weather-card one">
				<div class="top">
					<div class="wrapper">
						<div class="mynav">
							<a href="javascript:;"><span class="lnr lnr-chevron-left"></span></a>
							<a href="javascript:;"><span class="lnr lnr-cog"></span></a>
						</div>
						<h1 class="heading">Clear night</h1>
						<h3 class="location">Dhaka, Bangladesh</h3>
                        <Slider percentage={percentage} onChange={onChange} /> 
                        <div class="flex-con1">
                    
					
                <div className='flex-itm float-left'>{secondsToHms(currentTime)}</div>
      <div className='flex-itm float-right'>{secondsToHms(duration)}</div>
                
				</div>
					</div>
				</div>
				<div class="flex-con">
                    
				<div class="flex-itm">	    
                    
      <audio
        ref={audioRef}
        onTimeUpdate={getCurrDuration}
        onLoadedData={(e) => {
          setDuration(e.currentTarget.duration.toFixed(2))
          
        }}
        src="music1.mp3"
      ></audio>
      <ControlPanel ChangeSrc={ChangeSrc}
        play={play}
        isPlaying={isPlaying}
        duration={duration}
        currentTime={currentTime}
      />   </div>
				</div>
			</div>
		</div>

		<div class="col-sm-12 col-lg-6">
			<div class="weather-card rain">
				<div class="top">
					<div class="wrapper">
						<div class="mynav">
							<a href="javascript:;"><span class="lnr lnr-chevron-left"></span></a>
							<a href="javascript:;"><span class="lnr lnr-cog"></span></a>
						</div>
						<h1 class="heading">Rainy day</h1>
						<h3 class="location">Sylhet, Bangladesh</h3>
						<p class="temp">
							<span class="temp-value">16</span>
							<span class="deg">0</span>
							<a href="javascript:;"><span class="temp-type">C</span></a>
						</p>
					</div>
				</div>
				<div class="bottom">
					<div class="wrapper">
						<ul class="forecast">
							<a href="javascript:;"><span class="lnr lnr-chevron-up go-up"></span></a>
							<li class="active">
								<span class="date">Yesterday</span>
								<span class="lnr lnr-sun condition">
									<span class="temp">22<span class="deg">0</span><span class="temp-type">C</span></span>
								</span>
							</li>
							<li>
								<span class="date">Tomorrow</span>
								<span class="lnr lnr-cloud condition">
									<span class="temp">18<span class="deg">0</span><span class="temp-type">C</span></span>
								</span>
							</li>
						</ul>
					</div>
				</div>
			</div>
		</div>
	</div>
</div>
  
     
     
  
    

       
    
 </>
    )
}
export default MusicCard;
